-- used in tests that use HSQL
DROP TABLE IF EXISTS oauth_client_details;
create table oauth_client_details
(
    client_id               VARCHAR(256) PRIMARY KEY COMMENT '客户端id',
    resource_ids            VARCHAR(256) COMMENT '资源id集合,多个资源时用逗号(,)分隔',
    client_secret           VARCHAR(256) COMMENT '客户端秘钥',
    scope                   VARCHAR(256) COMMENT '权限范围,多个用逗号(,)分隔,可选：read,write,trust',
    authorized_grant_types  VARCHAR(256) COMMENT '支持的grant_type,可选值包括authorization_code,password,refresh_token,implicit,client_credentials, 若支持多个grant_type用逗号(,)分隔',
    web_server_redirect_uri VARCHAR(256) COMMENT '客户端的重定向URI,可为空, 当grant_type为authorization_code或implicit时, 在Oauth的流程中会使用并检查与注册时填写的redirect_uri是否一致',
    authorities             VARCHAR(256) COMMENT '客户端所拥有的Spring Security的权限值,可选, 若有多个权限值,用逗号(,)分隔, 如: "ROLE_UNITY,ROLE_USER"',
    access_token_validity   INTEGER COMMENT '客户端的access_token的有效时间值(单位:秒), 可选, 若不配置该属性则默认为12小时',
    refresh_token_validity  INTEGER COMMENT '客户端的refresh_token的有效时间值(单位:秒),可选, 若不设定值则使用默认的有效时间值(30天)',
    additional_information  VARCHAR(4096) COMMENT '预留的字段',
    autoapprove             VARCHAR(256) COMMENT '是否自动Approval操作, 默认值为 false, 可选值包括 true,false,read,write'
);
