DROP TABLE IF EXISTS oauth_client_token;
create table oauth_client_token
(
    token_id          VARCHAR(256),
    token             blob,
    authentication_id VARCHAR(256) PRIMARY KEY,
    user_name         VARCHAR(256),
    client_id         VARCHAR(256)
);

DROP TABLE IF EXISTS oauth_access_token;
create table oauth_access_token
(
    token_id          VARCHAR(256),
    token             blob,
    authentication_id VARCHAR(256) PRIMARY KEY,
    user_name         VARCHAR(256),
    client_id         VARCHAR(256),
    authentication    blob,
    refresh_token     VARCHAR(256)
);

DROP TABLE IF EXISTS oauth_refresh_token;
create table oauth_refresh_token
(
    token_id       VARCHAR(256),
    token          blob,
    authentication blob
);

DROP TABLE IF EXISTS oauth_code;
create table oauth_code
(
    code           VARCHAR(256),
    authentication blob
);
INSERT INTO `oauth_code` VALUES ('gvMF5X', );


DROP TABLE IF EXISTS oauth_approvals;
create table oauth_approvals
(
    userId         VARCHAR(256),
    clientId       VARCHAR(256),
    partnerKey   VARCHAR(32),
    scope          VARCHAR(256),
    status         VARCHAR(10),
    expiresAt      TIMESTAMP,
    lastModifiedAt TIMESTAMP NULL DEFAULT NULL
);
INSERT INTO `oauth_approvals` VALUES ('torlesse', 'torlesse002', NULL, 'TEST', 'APPROVED', '2022-05-13 17:19:25', '2022-04-13 17:19:25');


-- customized oauth_client_details table
DROP TABLE IF EXISTS ClientDetails;
create table ClientDetails
(
    appId                  VARCHAR(256) PRIMARY KEY,
    resourceIds            VARCHAR(256),
    appSecret              VARCHAR(256),
    scope                  VARCHAR(256),
    grantTypes             VARCHAR(256),
    redirectUrl            VARCHAR(256),
    authorities            VARCHAR(256),
    access_token_validity  INTEGER,
    refresh_token_validity INTEGER,
    additionalInformation  VARCHAR(4096),
    autoApproveScopes      VARCHAR(256)
);

-- 用户表
DROP TABLE IF EXISTS `t_user`;
CREATE TABLE `t_user`
(
    `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '自增id',
    `username`  varchar(32) NULL DEFAULT NULL,
    `password`  varchar(64) NULL DEFAULT NULL,
    `telephone` varchar(11) NULL DEFAULT NULL COMMENT '手机号',
    `enabled`   int(0) NULL DEFAULT 1 COMMENT '默认1  1有效 0 无效',
    PRIMARY KEY (`id`) USING BTREE,
    UNIQUE INDEX `idx_username`(`username`) USING BTREE
);
